\t\t\t\$h = \$imh * \$cw/\$imw;
\t\t\t\$w = \$cw;
\t\t\tif (\$h < \$ch) {
\t\t\t\t\$w = \$w * \$h/\$ch;
\t\t\t\t\$h = \$ch;
\t\t\t}
\t\t}
\t\telse {
\t\t\tif (stristr(\$size['w'] ,'%')) {
\t\t\t\t\$size['w'] += 0;
\t\t\t\t\$size['w'] /= 100;
\t\t\t\t\$size['w'] = (\$cw * \$size['w']);
\t\t\t}
\t\t\tif (stristr(\$size['h'] ,'%')) {
