\t\t}
\t\telse {
\t\t\tif (\$upp) {
\t\t\t\tif (count(\$str)) { \$bits[] = array(\$upp, \$str, false); }
\t\t\t\t\$str = array();
\t\t\t}
\t\t\t\$str[] = \$char;
\t\t\t\$upp = false;
\t\t}
\t   }
\t}
\telse {
\t   for(\$i=0;\$i<strlen(\$txt);\$i++) {
\t\tif (isset(\$this->upperCase[ord(\$txt[\$i])]) && \$this->upperCase[ord(\$txt[\$i])] < 256) {
\t\t\tif (!\$upp) {
\t\t\t\tif (count(\$str)) { \$bits[] = array(\$upp, \$str, false); }
\t\t\t\t\$str = array();
\t\t\t}
\t\t\t\$str[] = \$this->upperCase[ord(\$txt[\$i])];
\t\t\t\$upp = true;
\t\t}
\t\telse {
\t\t\tif (\$upp) {
\t\t\t\tif (count(\$str)) { \$bits[] = array(\$upp, \$str, false); }
\t\t\t\t\$str = array();
\t\t\t}
\t\t\t\$str[] = ord(\$txt[\$i]);
\t\t\t\$upp = false;
\t\t}
\t   }
\t}
