\t\t// Set (temporary) values for x y w h to do first paint, if values are auto
\t\tif (\$inner_h==='auto' && \$bbox_top==='auto') {
\t\t\t\$y = \$cont_y + \$bbox_mt + \$bbox_bt + \$bbox_pt;
