\t\t}
\t\t\$bbox_y = \$cont_y + \$bbox_top + \$bbox_mt;
\t\t\$bbox_x = \$cont_x + \$bbox_left + \$bbox_ml;
\t\t\$saved_block1 = \$this->blk[1];
