\t\$prop[1] = strtolower(\$prop[1]);
\tif (in_array(\$prop[1],\$this->borderstyles) && \$bsize > 0) { \$on = 1; }
\telse if (\$prop[1] == 'hidden') { \$on = 1; \$bsize = 0; \$coul = ''; }
