\t\t\t\t\t}

\t\t\t\t}
\t\t\t}
\t\t\telse {
\t\t\t\t\$wi = (\$table['w'] - (\$wis + \$tblbw));\t//\ti.e. extra space to distribute
\t\t\t\tforeach (\$list as \$k) {
