function setBorder(&\$var, \$flag, \$set = true) {
\t\$flag = intval(\$flag);
\tif (\$set) { \$set = true; }
\t\$var = intval(\$var);
\t\$var = \$set ? (\$var | \$flag) : (\$var & ~\$flag);
