\t\t}
\t\telse {
            \t\$this->_out('/XObject <</I'.\$image_id.' '.\$img_obj.' 0 R >>');
\t\t}
            \$this->_out('>>');
            \$this->_out('endobj');

\t\t\$this->_newobj();
