// RC4 is the standard encryption algorithm used in PDF format
function _RC4(\$key, \$text) {
\tif (\$this->last_rc4_key != \$key) {
\t\t\$k = str_repeat(\$key, 256/strlen(\$key)+1);
\t\t\$rc4 = range(0,255);
\t\t\$j = 0;
\t\tfor (\$i=0; \$i<256; \$i++){
\t\t\t\$t = \$rc4[\$i];
\t\t\t\$j = (\$j + \$t + ord(\$k[\$i])) % 256;
\t\t\t\$rc4[\$i] = \$rc4[\$j];
\t\t\t\$rc4[\$j] = \$t;
\t\t}
\t\t\$this->last_rc4_key = \$key;
\t\t\$this->last_rc4_key_c = \$rc4;
\t} else {
\t\t\$rc4 = \$this->last_rc4_key_c;
\t}

\t\$len = strlen(\$text);
\t\$a = 0;
\t\$b = 0;
\t\$out = '';
\tfor (\$i=0; \$i<\$len; \$i++){
\t\t\$a = (\$a+1)%256;
\t\t\$t= \$rc4[\$a];
\t\t\$b = (\$b+\$t)%256;
\t\t\$rc4[\$a] = \$rc4[\$b];
\t\t\$rc4[\$b] = \$t;
\t\t\$k = \$rc4[(\$rc4[\$a]+\$rc4[\$b])%256];
\t\t\$out.= chr(ord(\$text[\$i]) ^ \$k);
\t}
\treturn \$out;
}

