\tif (\$_y == null) { \$_y = \$y; }
\tif (\$_x === -1) { \$_x = \$this->x; }
\tif (\$_y === -1) { \$_y = \$this->y; }

\t\$wh = \$this->getTemplateSize(\$tplidx,\$_w,\$_h);
