\t\$out .= sprintf(\"q %.4F 0 0 %.4F %.2F %.2F cm\", (\$_w/\$this->tpls[\$tplidx]['box']['w']), (\$_h/\$this->tpls[\$tplidx]['box']['h']), \$_x*_MPDFK, (\$this->h-(\$_y+\$_h))*_MPDFK).\"\\n\";
\t\$out .= \$this->tplprefix.\$tplidx.\" Do Q\\n\";

\t\$s = array(\"w\" => \$_w, \"h\" => \$_h);
\t\$out .= \"Q\\n\";
\t\$this->pages[\$this->page] = \$out . \$this->pages[\$this->page];
\treturn \$s;
}
